using System; using System.Collections.Generic; using Xunit;  namespace ToDoAppTest {     public class UnitTest1     {         [Fact]         public void CheckIfRemoved()         {             var task = new ToDoApp.Task("Task no1", 1);             var task2 = new ToDoApp.Task("Task no2", 2);             var task3 = new ToDoApp.Task("Task no3", 3);              List<ToDoApp.Task> list = new List<ToDoApp.Task> { task, task2, task3 };             List<ToDoApp.Task> excpectedlist = new List<ToDoApp.Task> { task, task2 };              var tdl = new ToDoApp.ToDoList();              tdl.DoTask(list, "Do #3");             Assert.Equal(list, excpectedlist);         }          [Fact]         public void CheckIfCorrectIdAssigned()         {             var task = new ToDoApp.Task("Task no1", 1);             var task2 = new ToDoApp.Task("Task no2", 2);              List<ToDoApp.Task> list = new List<ToDoApp.Task> { task, task2 };             var expected_ID = 3;              var tdl = new ToDoApp.ToDoList();             var ID = tdl.AssignId(list);              Assert.Equal(expected_ID, ID);         }      } }  